import { useState } from "react";
import NoteContex from "./NoteContex";
const NoteState=(props)=>{
    const s1={
        "name":"herry",
        "class":"5b"
    }
  const[state,setState] = useState(s1)
  const update=()=>{
    setTimeout(() => {
        setState({
            "name":"larry",
            "class":"6c"
        })
    }, 1000);
  }
return (
    <NoteContex.Provider value={{state,update}}>

       { props.children}
    </NoteContex.Provider>
)
}

export default NoteState
