import { useState } from "react";
import NoteContex from "./NoteContex";
const NoteState = (props) => {
  const host = 'http://localhost:4000'
  const initialnotes = []
  const [notes, setNotes] = useState(initialnotes)

  //  add note
  const addNote =async (title, description, tag) => {
    // api call 
    const response = await fetch(`${host}/api/notes/addnotes`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjJhYjg5YTU5ZTZmZWY5ODY0YzZiNzhhIn0sImlhdCI6MTY1NTQ1NTE3MX0.7Y-7ZyeVYxVh9xwkOZrszcK2nr2XCLDmcPiEBmZg_wE"

      },

      body: JSON.stringify({title,description,tag})
    });




    console.log('adding a note');
    const note = {
      "_id": "62acde8b6feca70de134894b06",
      "user": "62ab89a59e6fef9864c6b78a",
      "title": title,
      "description": description,
      "tag": tag,
      "date": "2022-06-17T20:05:31.907Z",
      "__v": 0
    }
    setNotes(notes.concat(note))
  }


  //  delete note

  const deleteNote = (id) => {
    console.log('deleting the note with id' + id);
    const newNote = notes.filter((note) => { return note._id !== id })
    setNotes(newNote)
  }


  //  edit note
  const editNote = async(id, title, description, tag) => {
    // Api call (google-fech with headers)

    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjJhYjg5YTU5ZTZmZWY5ODY0YzZiNzhhIn0sImlhdCI6MTY1NTQ1NTE3MX0.7Y-7ZyeVYxVh9xwkOZrszcK2nr2XCLDmcPiEBmZg_wE"

      },

      body: JSON.stringify({title,description,tag})
    });
    const json= response.json();



  for (let index = 0; index < notes.length; index++) {
    const element = notes[index];
    if (element._id === id) {
      element.title = title
      element.description = description
      element.tag = tag
    }

  }

}


return (
  <NoteContex.Provider value={{ notes, setNotes, addNote, deleteNote, editNote }}>

    {props.children}
  </NoteContex.Provider>
)
}

export default NoteState
